            // }
          />
          <TextField
            id="outlined-basic"
            label="Password"
            variant="outlined"
            type="password"
            name="password"
            fullWidth
            className={classes.inputField}
            inputRef={register}
            // value={password}
            // onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
            //   setPassword(e.target.value)
            // }
          />
          <Button
            type="submit"
            size="large"
            color="primary"
            variant="contained"
            fullWidth
          >
            Login!
          </Button>
        </form>
        <Typography style={{ marginTop: '1rem' }}>
          Don't have an account? <a href="/register">Register Here</a>
        </Typography>
      </Paper>
    </div>
  );
};

export default LoginPage;
